package bit701.day0901;

public class Ex99_star {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int i=0; i<=17; i+=2) {
			for(int j=1; j<i; j++){
				System.out.print("*");
			}
			System.out.println();

		}
		for(int m=14; m>=1; m-=2) {
			for(int n=1; n<m; n++){
				System.out.print("*");
			}
			System.out.println();

		
	}

	}
}
